/* Typing text effect */
.typing-text {
  font-size: 1.5rem;
  color: var(--primary-color);
  border-right: 2px solid var(--primary-color);
  white-space: nowrap;
  overflow: hidden;
  margin: 0 auto;
  letter-spacing: 0.15em;
}

@keyframes blink-caret {
  from, to { border-color: transparent }
  50% { border-color: var(--primary-color); }
}
#timeline {
    z-index: 2;
    margin-top: 4rem;
    margin-bottom: 4rem;
    padding: 4rem 2rem;
}

.timeline-container {
    position: relative;
    max-width: 800px;
    margin: 0 auto;
}

.timeline-container::after {
    content: '';
    position: absolute;
    width: 6px;
    background-color: var(--secondary-color);
    top: 0;
    bottom: 0;
    left: 50%;
    margin-left: -3px;
}

.timeline-item {
    padding: 10px 40px;
    position: relative;
    background-color: inherit;
    width: 50%;
}

.timeline-content {
    padding: 20px 30px;
    background-color: var(--primary-color);
    position: relative;
    border-radius: 6px;
    color: var(--background-color);
}

.timeline-item::after {
    content: '';
    position: absolute;
    width: 25px;
    height: 25px;
    right: -17px;
    background-color: var(--background-color);
    border: 4px solid var(--accent-color);
    top: 15px;
    border-radius: 50%;
    z-index: 1;
}

.timeline-item:nth-child(odd) { left: 0; }
.timeline-item:nth-child(even) { left: 50%; }
.timeline-item:nth-child(even)::after { left: -16px; }

.cyber-background {
    position: fixed;
    inset: 0;
    z-index: -1;
    background: 
        linear-gradient(to bottom, rgba(10, 10, 10, 0.8) 0%, rgba(10, 10, 10, 0.2) 100%),
        repeating-linear-gradient(0deg, transparent 0px, transparent 2px, rgba(0, 204, 255, 0.1) 2px, rgba(0, 204, 255, 0.1) 4px);
    overflow: hidden;
}

.cyber-grid {
    position: absolute;
    width: 200%;
    height: 200%;
    top: -50%;
    left: -50%;
    background-image: 
        linear-gradient(to right, rgba(0, 255, 0, 0.1) 1px, transparent 1px),
        linear-gradient(to bottom, rgba(0, 255, 0, 0.1) 1px, transparent 1px);
    background-size: 50px 50px;
    transform: perspective(500px) rotateX(60deg);
    animation: moveGrid 20s linear infinite;
}

.cyber-particles {
    position: absolute;
    width: 100%;
    height: 100%;
}

.particle {
    position: absolute;
    width: 2px;
    height: 2px;
    background-color: var(--accent-color);
    box-shadow: 0 0 10px var(--accent-color);
    border-radius: 50%;
    animation: floatParticle linear infinite;
}

.cnn-logo {
    width: 40px;
    height: 40px;
    display: inline-block;
    vertical-align: middle;
    transition: transform 0.3s ease;
    position: relative;
}

.cnn-logo:hover {
    transform: scale(1.2);
}

.cnn-logo svg {
    width: 100%;
    height: 100%;
}

.cnn-layer {
    fill: none;
    stroke: var(--primary-color);
    stroke-width: 2;
    transition: stroke 0.3s ease;
}

.cnn-connection {
    fill: none;
    stroke: var(--accent-color);
    stroke-width: 1;
    stroke-dasharray: 5;
    stroke-dashoffset: 1000;
    animation: flowAnimation 5s linear infinite;
}

.cnn-node {
    fill: var(--secondary-color);
    animation: pulseAnimation 2s ease-in-out infinite;
}

@keyframes flowAnimation {
    to {
        stroke-dashoffset: 0;
    }
}

@keyframes pulseAnimation {
    0%, 100% {
        transform: scale(1);
        opacity: 1;
    }
    50% {
        transform: scale(1.2);
        opacity: 0.7;
    }
}

.cnn-logo:hover .cnn-layer {
    stroke: var(--secondary-color);
}

@keyframes spin {
    0% { transform: rotateY(0deg); }
    100% { transform: rotateY(360deg); }
}

@keyframes scroll {
    0%, 100% { transform: translate(-50%, 0); opacity: 0; }
    50% { opacity: 1; }
    100% { transform: translate(-50%, 20px); }
}

@keyframes holographic-flicker {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.8; }
}

@keyframes pulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
}

@keyframes hue-rotate {
    0% { filter: hue-rotate(0deg); }
    100% { filter: hue-rotate(360deg); }
}

@keyframes glitch-move {
    0% { transform: translateY(0); }
    100% { transform: translateY(2px); }
}

@keyframes floatParticle {
    from { transform: translateY(0) translateX(0); opacity: 1; }
    to { transform: translateY(-100vh) translateX(100px); opacity: 0; }
}

@keyframes moveGrid {
    from { background-position: 0 0; }
    to { background-position: 50px 50px; }
}

@keyframes noise-anim-2 {
    0% { clip: rect(12px, 9999px, 52px, 0); }
    5% { clip: rect(48px, 9999px, 24px, 0); }
    10% { clip: rect(60px, 9999px, 93px, 0); }
    /* ... add more keyframes as desired ... */
    100% { clip: rect(79px, 9999px, 56px, 0); }
}

@media screen and (max-width: 600px) {
    .timeline-container::after { left: 31px; }
    .timeline-item {
        width: 100%;
        padding-left: 70px;
        padding-right: 25px;
    }
    .timeline-item:nth-child(even) { left: 0; }
    .timeline-item::after {
        left: 15px;
        right: auto;
    }
    .timeline-content { padding: 15px 20px; }
}

@media screen and (max-width: 768px) {
    .holographic-image-wrapper {
        padding-top: 75%; /* Adjust for mobile view if needed */
    }
}
